import React, { useEffect, useState } from 'react';
import {
  Table,
  Space,
  Button,
  Popconfirm,
  message,
  Image,
  Form,
  Input,
} from 'antd';
import { useRequest } from 'umi';
import { bannerGet, bannerUpdate } from './../../api/coke';
import Edit from './edit';
import ImgUpload from '@/components/imgUpload';

export default function BannerList() {
  const [form] = Form.useForm();
  const [data1, setData] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [img, setImg] = useState(null);
  const [currentId, setCurrentId] = useState(null);

  let { data, loading, error } = useRequest(() =>
    bannerGet().then((res) => {
      setData(res.data);
    }),
  );

  // 删除
  const handleOk = (id) => {};

  // 编辑
  const editCancel = () => {
    setIsModalOpen(false);
  };
  const onFinish = (values) => {
    bannerUpdate(currentId, values).then((res) => {
      bannerGet().then((res1) => {
        setData(res1.data);
        message.success('修改成功');
        setIsModalOpen(false);
      });
    });
  };

  const columns = [
    {
      title: '活动名称',
      dataIndex: 'bannername',
    },
    {
      title: '活动链接',
      dataIndex: 'bannerlink',
      render: (url) => (
        <a href={url} target="_blink">
          {url}
        </a>
      ),
    },
    {
      title: '活动封面',
      dataIndex: 'bannerimg',
      render: (url) => (
        <div style={{ width: 110, textAlign: 'center' }}>
          <Image
            width="100%"
            src={url}
            fallback="data:image/png;base64,"
          />
        </div>
      ),
    },
    {
      title: '操作',
      render: (h) => (
        <Space>
          <Button
            type="primary"
            size="small"
            onClick={() => {
              setIsModalOpen(!isModalOpen);
              form.setFieldsValue(h);
              setImg(h.bannerimg);
              setCurrentId(h.objectId);
            }}
          >
            编辑
          </Button>
          <Popconfirm title="确定删除？" onConfirm={() => handleOk(h.objectId)}>
            <Button type="primary" size="small" danger>
              删除
            </Button>
          </Popconfirm>
        </Space>
      ),
    },
  ];
  return (
    <div>
      <Edit title="编辑活动" isModalOpen={isModalOpen} editCancel={editCancel}>
        <Form
          form={form}
          name="control-hooks"
          onFinish={onFinish}
          style={{
            maxWidth: 600,
          }}
        >
          <Form.Item
            name="bannername"
            label="活动名称"
            rules={[
              {
                required: true,
              },
            ]}
          >
            <Input />
          </Form.Item>
          <Form.Item
            name="bannerlink"
            label="活动链接"
            rules={[
              {
                required: true,
              },
            ]}
          >
            <Input />
          </Form.Item>
          <Form.Item
            name="bannerimg"
            label="活动图片"
            rules={[
              {
                required: true,
              },
            ]}
          >
            <ImgUpload img={img} />
          </Form.Item>
          <Form.Item
            wrapperCol={{
              offset: 20,
              span: 16,
            }}
          >
            <Space>
              <Button type="primary" htmlType="submit">
                更新
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Edit>
      <Table
        columns={columns}
        dataSource={data1}
        rowKey="objectId"
        loading={loading}
      />
    </div>
  );
}
